                builder: (_) => const TimelineScreen(),
              );
      case login:
        return MaterialPageRoute(builder: (_) => const SigninScreen());
      case register:
        return MaterialPageRoute(builder: (_) => const SignupScreen());
      default:
        return MaterialPageRoute(
            builder: (_) => ErrorWidget('Route not found'));
    }
  }
}

