        'usernames': FieldValue.arrayUnion([state['username']]),
      })
      // Add email to email list
      ..collection('credentials').doc('emails').update({
        'emails': FieldValue.arrayUnion([state['email']]),
      });
    return Future.value(true);
  }
}

